var stat = global.get("Sonoff1_webstat");

if (stat===undefined) {
    stat = {count:0, OK:0, ECONNRESET:0, ENOTFOUND:0, ETIMEDOUT:0, other:0};
}
switch (msg.statusCode) {
    case 200:
        stat.count++;
        stat.OK++;
        break;
    case "ECONNRESET":
        stat.count++;
        stat.ECONNRESET++;
        break;
    case "ENOTFOUND":
        stat.count++;
        stat.ENOTFOUND++;
        break;
    case "ETIMEDOUT":
        stat.count++;
        stat.ETIMEDOUT++;
        break;
    default:    
        stat.count++;
        stat.other++;
}

global.set("Sonoff1_webstat",stat);

return [
    {topic:"OK", payload:stat.OK},
    {topic:"Connection reset", payload:stat.ECONNRESET},
    {topic:"Timed out", payload:stat.ETIMEDOUT},
    {topic:"Not found", payload:stat.ENOTFOUND},
    {topic:"Other error", payload:stat.other}
    ];

